import axios from "axios";
import { response } from "express";
import { createContext, useContext, useEffect } from "react";
import { useState } from "react";
import { YT_API_URL } from "../API";


const AppContext = createContext(null);

export const useAppContext = () =>{

    const context = useContext(AppContext);
    if(!context ){

        throw new Error('useAppcontext must be within appContextProvider!');
    };
    return context;
};

const AppContextProvider = ({ children }) => {
    const [videos, setVideos] = useState([]);

    // Fetch video list on component mount
    useEffect(() => {
        axios.get(YT_API_URL)
            .then(res => {
                console.log(res.data);
                setVideos(res.data.items); // Assuming API returns 'items'
            })
            .catch((error) => console.log(error));
    }, []);

    return (
        <AppContext.Provider value={{ videos }}>
            {children}
        </AppContext.Provider>
    );
};

